import React, { useState, useEffect } from "react";
import "./HamburgerMenu.css"; // Import the styles

export default function HamburgerMenu() {
  const [isOpen, setIsOpen] = useState(false); // Controls menu open/close
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768); // Tracks screen size

  // Toggle menu visibility
  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  // Detect screen resize
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
      if (window.innerWidth >= 768) {
        setIsOpen(false); // Close menu when resizing to desktop
      }
    };

    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  return (
    <nav className="navbar">
      <div className="logo"></div>

      {/* Hamburger Icon - Shown only on mobile */}
      {isMobile && (
        <div className="hamburger" onClick={toggleMenu}>
          â˜°
        </div>
      )}

      {/* Navigation Links */}
      <ul
        className={`nav-links ${isMobile ? (isOpen ? "open" : "closed") : ""}`}
      >
        <li>
          <a href="#">Home</a>
        </li>
        <li>
          <a href="#">About</a>
        </li>
        <li>
          <a href="#">Services</a>
        </li>
        <li>
          <a href="#">Contact</a>
        </li>
      </ul>
    </nav>
  );
}
