import React, { useState } from "react";
import axios from "axios";
import "./Chat1.css";

export default function Chat1() {
  const [msg, setMsg] = useState("");
  const [msgs, setMsgs] = useState([]); // Stores user messages
  const [botMsgs, setBotMsgs] = useState([]); // Stores bot replies

  const HandleSubmit = async (e) => {
    e.preventDefault();

    if (!msg.trim()) return; // Prevent empty messages

    setMsgs((prevMsgs) => [...prevMsgs, msg]); // Store user message
    setMsg(""); // Clear input field
    try {
      // Send user message to the backend
      const response = await axios.post("http://localhost:5001/msg", {
        message: msg,
      });

      const botReply = response.data.reply; // Get bot response

      // Update state to display both user and bot messages
      setBotMsgs((prevBotMsgs) => [...prevBotMsgs, botReply]); // Store bot reply
    } catch (error) {
      console.error("Error sending message:", error);
    }
  };

  return (
    <div>
      <div className="main">
        <h3>Chat</h3>
        <ul>
          {msgs.map((message, index) => (
            <li key={index}>
              <strong>User:</strong> {message}
              <br />
              <strong>Bot:</strong> {botMsgs[index] || "Typing..."}
            </li>
          ))}
        </ul>
        <form onSubmit={HandleSubmit}>
          <input
            type="text"
            placeholder="Message here..."
            onChange={(e) => setMsg(e.target.value)}
            value={msg}
          />
          <button type="submit">Send</button>
        </form>
      </div>
    </div>
  );
}
