import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import "./Chat1.css";

export default function Chat1() {
  const [msg, setMsg] = useState("");
  const [msgs, setMsgs] = useState([]);
  const [botMsgs, setBotMsgs] = useState([]);
  const msgBodyRef = useRef(null);

  useEffect(() => {
    if (msgBodyRef.current) {
      msgBodyRef.current.scrollTop = msgBodyRef.current.scrollHeight;
    }
  }, [msgs]);

  const HandleSubmit = async (e) => {
    e.preventDefault();

    if (!msg.trim()) return;

    setMsgs((prevMsgs) => [...prevMsgs, msg]);
    setMsg("");
    try {
      const response = await axios.post("http://localhost:5001/msg", {
        message: msg,
      });

      const botReply = response.data.reply;

      setBotMsgs((prevBotMsgs) => [...prevBotMsgs, botReply]);
    } catch (error) {
      console.error("Error sending message:", error);
    }
  };

  return (
    <div>
      <div className="main">
        <div className="child-main">
          <h3>Chat</h3>
          <div className="ul">
            {msgs.map((message, index) => (
              <div className="parent-msg" ref={msgBodyRef} key={index}>
                <div className="user-msg msg">{message}</div>
                {/* <br /> */}
                <div className="bot-msg msg">{botMsgs[index]}</div>
              </div>
            ))}
          </div>
          <form onSubmit={HandleSubmit}>
            <input
              type="text"
              placeholder="Message here..."
              onChange={(e) => setMsg(e.target.value)}
              value={msg}
            />
            <button type="submit">Send</button>
          </form>
        </div>
      </div>
    </div>
  );
}
