import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Chat1() {
  const [msg, setMsg] = useState("");
  const [msgs, setMsgs] = useState([]); // Store multiple messages
  const [flagforMsg, setFlagForMsg] = useState(false); // Use state

  useEffect(() => {
    axios
      .get("http://localhost:5001/")
      .then((response) => {
        setMsgs(response.data);
        console.log(response.data);
      })
      .catch((e) => {
        console.log("Errorrr:", e);
      });
  }, []);

  useEffect(() => {
    console.log(msgs);
  }, [msgs]);

  const HandleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5001/msg", { message: msg });
      setMsgs((prevMsgs) => [...prevMsgs, msg]); // Append the new message
      setMsg(""); // Clear input field
      setFlagForMsg(true); // Trigger re-render
    } catch (error) {
      console.error("Error sending message:", error);
    }
  };

  return (
    <div>
      <div>
        {/* Render the latest message */}
        <div>Message: {flagforMsg && msg}</div>
        <form onSubmit={HandleSubmit}>
          <input
            type="text"
            placeholder="Message here..."
            onChange={(e) => setMsg(e.target.value)}
            value={msg}
          />
          <button type="submit">Send</button>
        </form>
      </div>
    </div>
  );
}
